import React from 'react';
import { useState } from 'react';
import api from '../../services/api';
import '../../global.css';

function App() {

  function gerarSVG(){

    const cidade = "Mauriti";

    const requestPolygonCity = new Request(`http://localhost:3333/getSvg/${cidade}`);
    const requestViewBoxCity = new Request(`http://localhost:3333/getViewBox/${cidade}`);


    fetch(requestPolygonCity)
        .then(response => {
            return response.json();     
        }).then(response => {
        document.getElementById('visivel').setAttribute('d', response[0].st_assvg)
        })

    fetch(requestViewBoxCity)
        .then(response => {
          return response.json();
        }).then(response =>{
          document.getElementById('svg').setAttribute('viewBox', response[0].getviewbox);
        })
    }

  return (
    <div className="main">
      <h1>Informções do DEV</h1>

      <button id="botao" onclick={gerarSVG()}>Gerar SVG</button>
        <svg id="svg">
        <path id="visivel"/>
        </svg>
    </div>
  ); 
}

export default App;
